CREATE OR REPLACE FUNCTION clinical_panorama_md_pck.get_smart_alert_color ( ie_severidade_p text) RETURNS varchar AS $body$
DECLARE

ds_color_w  varchar(200);

BEGIN	
	if ie_severidade_p = 'S' then
		ds_color_w := 'sentry-smart-alert-danger';
	else
		ds_color_w := 'sentry-smart-alert-warning';
	end if;
	return ds_color_w;
	
END;

