CREATE OR REPLACE FUNCTION clipboard_util_pkg.format_string ( field_p text, type_p text ) RETURNS varchar AS $body$
DECLARE

        return_w varchar(2000);

BEGIN
        IF ( field_p = 'NA' ) THEN
            return_w := '';
        ELSE
            return_w := chr(38)
            ||type_p
                        || '-'
                        || field_p
                        || '<br/>';
        END IF;

        RETURN return_w;
    END;

$body$
