SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION clipboard_util_pkg.get_content_cur_med ( nr_atendimento_p bigint, nr_sequencia_p text, ie_record_type_p text, config_option_p bigint ) RETURNS varchar AS $body$
DECLARE


        ds_content_w   varchar(32000);
        st_dt_w        varchar(1000);
        dept_w         varchar(1000);
        material_w     varchar(1000);
        unit_w         varchar(1000);
        intrl_w        varchar(1000);
        comnts_w       varchar(1000);
        qt_dose_dia_w  varchar(1000);
        c01 CURSOR FOR
        SELECT
            *
        FROM
            TABLE( clipboard_util_pkg.get_clipboard_rule(ie_record_type_p) )
        WHERE
            nr_seq = config_option_p
        ORDER BY
            nr_seq_display ASC;


  i RECORD;
BEGIN
        FOR i IN (WITH RECURSIVE cte AS (

            SELECT
                regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS data_w

            (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')
          UNION ALL

            SELECT
                regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS data_w
            
            (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')
         JOIN cte c ON ()

) SELECT * FROM cte;
) LOOP
            SELECT
                pkg_date_formaters.to_varchar(a.dt_inicio, 'shortDate', wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck
                .get_nm_usuario),
                obter_ds_setor_atendimento(a.cd_setor_atendimento),
                obter_desc_material(a.cd_material),
                a.qt_dosagem || a.cd_unidade_medida,
                obter_desc_intervalo_prescr(a.cd_intervalo),
                a.qt_dose_dia
            INTO STRICT
                st_dt_w,
                dept_w,
                material_w,
                unit_w,
                intrl_w,
                qt_dose_dia_w
            FROM
                cpoe_material     a
                
            WHERE
              
                 a.nr_sequencia = i.data_w
                AND a.cd_pessoa_fisica = clipboard_util_pkg.get_pessao_fisica(nr_atendimento_p)  LIMIT 1;

        select cpoe_get_list_of_comments(i.data_w,'CPOE_MATERIAL','DC') into STRICT comnts_w;

            FOR r_c01 IN c01 LOOP IF ( ie_record_type_p = 'CUR_MED' ) THEN
                IF ( r_c01.ie_field = 'START_DT' ) THEN
                    IF (st_dt_w IS NOT NULL AND st_dt_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || st_dt_w;

                    END IF;
                END IF;

                IF ( r_c01.ie_field = 'MED_DEPT' ) THEN
                    IF (dept_w IS NOT NULL AND dept_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || dept_w;

                    END IF;

                END IF;

                IF ( r_c01.ie_field = 'DRG_NM' ) THEN
                    IF (material_w IS NOT NULL AND material_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || material_w;

                    END IF;
                END IF;

                IF ( r_c01.ie_field = 'DOSAGE' ) THEN
                    IF (unit_w IS NOT NULL AND unit_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || unit_w;

                    END IF;
                END IF;

                IF ( r_c01.ie_field = 'USAGE' ) THEN
                    IF (intrl_w IS NOT NULL AND intrl_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || intrl_w;

                    END IF;
                END IF;

                IF ( r_c01.ie_field = 'COMMENTS' ) THEN
                    IF (comnts_w IS NOT NULL AND comnts_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || comnts_w;

                    END IF;
                END IF;
                IF ( r_c01.ie_field = 'DAILY_DOSE' ) THEN
                    IF (qt_dose_dia_w IS NOT NULL AND qt_dose_dia_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || qt_dose_dia_w;

                    END IF;
                END IF;
            END IF;
            END LOOP;

            ds_content_w := ds_content_w || '</br>';
        END LOOP;

        RETURN ds_content_w;
    END;

  /* generic function to concat */

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
