SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION clipboard_util_pkg.get_content_family_hist ( nr_atendimento_p bigint, nr_sequencia_p text, ie_record_type_p text, config_option_p bigint ) RETURNS varchar AS $body$
DECLARE


        ds_content_w   varchar(32000);
        rel_ship_w     varchar(1000);
        fam_hist_w     varchar(1000);
        c01 CURSOR FOR
        SELECT
            *
        FROM
            TABLE( clipboard_util_pkg.get_clipboard_rule(ie_record_type_p) )
        WHERE
            nr_seq = config_option_p
        ORDER BY
            nr_seq_display ASC;


  i RECORD;
BEGIN
        FOR i IN (WITH RECURSIVE cte AS (

            SELECT
                regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS data_w

            (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')
          UNION ALL

            SELECT
                regexp_substr(nr_sequencia_p, '[^,]+', 1, level) AS data_w
            
            (regexp_substr(nr_sequencia_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_sequencia_p, '[^,]+', 1, level))::text <> '')
         JOIN cte c ON ()

) SELECT * FROM cte;
) LOOP
            SELECT
                obter_desc_parentesco(nr_seq_parentesco),
                obter_desc_cid_doenca(cd_doenca)
            INTO STRICT
                rel_ship_w,
                fam_hist_w
            FROM
                paciente_antec_clinico
            WHERE
                cd_pessoa_fisica = clipboard_util_pkg.get_pessao_fisica(nr_atendimento_p)
                AND nr_sequencia = i.data_w;

            FOR r_c01 IN c01 LOOP IF ( ie_record_type_p = 'FAM_HIST' ) THEN
                IF ( r_c01.ie_field = 'FAM_HIST' ) THEN
                    IF (fam_hist_w IS NOT NULL AND fam_hist_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || fam_hist_w;

                    END IF;
                END IF;

                IF ( r_c01.ie_field = 'RELATION' ) THEN
                    IF (rel_ship_w IS NOT NULL AND rel_ship_w::text <> '') THEN
                        ds_content_w := ds_content_w
                                        || '</br>'
                                        || coalesce(r_c01.nm_field, r_c01.ds_expressao)
                                        || ' - '
                                        || rel_ship_w;

                    END IF;

                END IF;

            END IF;
            END LOOP;

            ds_content_w := ds_content_w || '</br>';
        END LOOP;

        RETURN ds_content_w;
    END;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
