CREATE OR REPLACE FUNCTION col_gerar_arquivo_rips.obter_dados_diag_posicao ( nr_atendimento_p atendimento_paciente.nr_atendimento%TYPE ) RETURNS SETOF COL_GERAR_ARQUIVO_RIPS.TABLE_DIAG_DOENCA AS $body$
DECLARE

        cd_doenca_w             diagnostico_doenca.cd_doenca%TYPE;
        cd_doenca_sec1_w        diagnostico_doenca.cd_doenca%TYPE;
        cd_doenca_sec2_w        diagnostico_doenca.cd_doenca%TYPE;
        cd_doenca_sec3_w        diagnostico_doenca.cd_doenca%TYPE;
        dt_liberacao_w          diagnostico_doenca.dt_liberacao%TYPE;
        rips_diag_doenca_type   COL_GERAR_ARQUIVO_RIPS.rips_diag_doenca_type;

    
BEGIN
        SELECT  MAX(xtab.cd_doenca),
                MAX(xtab.dt_liberacao)
        INTO STRICT    cd_doenca_w,
                dt_liberacao_w
        FROM (SELECT    D.cd_doenca,
                        D.dt_liberacao
                FROM    diagnostico_doenca D
                WHERE   D.ie_classificacao_doenca = 'P'
                AND     D.ie_diag_admissao = 'S'
                AND     D.ie_situacao = 'A'
                AND     (D.dt_liberacao IS NOT NULL AND D.dt_liberacao::text <> '')
                AND     D.nr_atendimento = nr_atendimento_p 
                ORDER BY dt_diagnostico DESC LIMIT 1) xtab;
                
        WITH diagnostico_doenca_secundario AS (
            SELECT      X.cd_doenca, row_number() OVER () AS linha
                FROM    diagnostico_doenca X
                WHERE   X.ie_classificacao_doenca = 'S'
                AND     (X.dt_liberacao IS NOT NULL AND X.dt_liberacao::text <> '') 
                AND     X.ie_situacao = 'A'
                AND     coalesce(X.dt_fim::text, '') = ''
                AND     X.nr_atendimento = nr_atendimento_p
                )
         SELECT (SELECT D.cd_doenca FROM diagnostico_doenca_secundario D WHERE D.linha = 1),
                (SELECT D.cd_doenca FROM diagnostico_doenca_secundario D WHERE D.linha = 2),
                (SELECT D.cd_doenca FROM diagnostico_doenca_secundario D WHERE D.linha = 3)
         INTO STRICT       cd_doenca_sec1_w,
                    cd_doenca_sec2_w,
                    cd_doenca_sec3_w
;

        rips_diag_doenca_type.cd_doenca_diag_primario    := cd_doenca_w;
        rips_diag_doenca_type.cd_doenca_diag_secundario1 := cd_doenca_sec1_w;
        rips_diag_doenca_type.cd_doenca_diag_secundario2 := cd_doenca_sec2_w;
        rips_diag_doenca_type.cd_doenca_diag_secundario3 := cd_doenca_sec3_w;

         RETURN NEXT rips_diag_doenca_type;
        RETURN;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
