CREATE OR REPLACE PROCEDURE comunic_interna_pck.inicializa_valores_package ( cd_estab_atual_p bigint, nm_usuario_p text) AS $body$
BEGIN
/* Essa procedure é para inicializar os valores quando necessário */

PERFORM set_config('comunic_interna_pck.cd_estabelecimento_atual_w', cd_estab_atual_p, false);
PERFORM set_config('comunic_interna_pck.nm_usuario_w', nm_usuario_p, false);

if (current_setting('comunic_interna_pck.nm_usuario_w')::usuario.nm_usuario%type <> coalesce(current_setting('comunic_interna_pck.nm_usuario_ant_w')::usuario.nm_usuario%type,'X')) then
	PERFORM set_config('comunic_interna_pck.cd_estabelecimento_ant_w', 0, false);
end if;
PERFORM set_config('comunic_interna_pck.nm_usuario_ant_w', nm_usuario_p, false);

end;


