SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE conciliar_integr_resp_rec_pck.atualizar_guia () AS $body$
DECLARE


current_setting('conciliar_integr_resp_rec_pck.i')::integer integer := 0;
ie_status_protocolo_w	imp_resp_recurso_prot.ie_status%type;


BEGIN

if (current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v.count > 0) then
	FOR current_setting('conciliar_integr_resp_rec_pck.i')::integer IN 0..campos_guia_w.count - 1 LOOP

			/*

				nfcunha - OS 2466841 - 11/Jun/2021
				Atualizar guia conforme status da tabela superior.

			*/


			select 	max(x.ie_status)
			into STRICT	ie_status_protocolo_w
			from	imp_resp_recurso_prot x
			where	x.nr_sequencia = current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_seq_res_rec_prot;

			if ie_status_protocolo_w <> 'C' then
				current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status := ie_status_protocolo_w;
			end if;

			update	imp_resp_recurso_guia
			set	dt_atualizacao 		= clock_timestamp(),
				nm_usuario     		= 'Integration',
				ie_status      		= current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status
			where	nr_seq_res_rec_prot 	= current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_seq_res_rec_prot
			and     nr_sequencia   		= current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_sequencia;

			if (current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status <> 'C') then

				update	imp_resp_recurso_item
				set	dt_atualizacao = clock_timestamp(),
					nm_usuario     = 'Integration',
					ie_status      = current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status
				where	nr_seq_res_rec_guia = current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_sequencia;

				current_setting('conciliar_integr_resp_rec_pck.campos_guia_w')::campos_guia_v.delete(current_setting('conciliar_integr_resp_rec_pck.i')::integer);
			end if;
	END LOOP;
end if;
commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE conciliar_integr_resp_rec_pck.atualizar_guia () FROM PUBLIC;
