CREATE OR REPLACE PROCEDURE conciliar_integr_resp_rec_pck.atualizar_lotes () AS $body$
DECLARE

current_setting('conciliar_integr_resp_rec_pck.i')::integer integer := 0;

BEGIN

if (current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v.count > 0) then
	FOR current_setting('conciliar_integr_resp_rec_pck.i')::integer IN 0..campos_lote_w.count - 1 LOOP

		update	imp_resp_recurso_prot
		set	dt_atualizacao = clock_timestamp(),
			nm_usuario     = 'Integration',
			ie_status      = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status,
			nr_seq_protocolo = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_seq_protocolo
		where	nr_sequencia   = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_sequencia;


		if (current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status <> 'C') then

			update	imp_resp_recurso_guia
			set	dt_atualizacao = clock_timestamp(),
				nm_usuario     = 'Integration',
				ie_status      = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status
			where	nr_seq_res_rec_prot = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_sequencia;


			update	imp_resp_recurso_item
			set	dt_atualizacao = clock_timestamp(),
				nm_usuario     = 'Integration',
				ie_status      = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).ie_status
			where	nr_seq_res_rec_guia in (SELECT	x.nr_sequencia
							from 	imp_resp_recurso_guia x 
							where 	x.nr_seq_res_rec_prot = current_setting('conciliar_integr_resp_rec_pck.campos_lote_w')::campos_lote_v(current_setting('conciliar_integr_resp_rec_pck.i')::integer).nr_sequencia);

		end if;
	END LOOP;
end if;
commit;

END;

$body$
