CREATE OR REPLACE FUNCTION conciliar_integr_resp_rec_pck.obter_dt_pagto_guia (cd_autorizacao_p lote_audit_hist_guia.cd_autorizacao%type, nr_interno_conta_p lote_audit_hist_guia.nr_interno_conta%type) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	lote_audit_hist_guia.dt_pagamento%type;


BEGIN

	begin
		select	max(dt_pagamento)
		into STRICT	dt_retorno_w
		from	convenio_retorno_item
		where	nr_interno_conta = nr_interno_conta_p
		and	coalesce(cd_autorizacao, current_setting('conciliar_integr_resp_rec_pck.not_informed_w')::varchar(100)) = coalesce(cd_autorizacao_p, current_setting('conciliar_integr_resp_rec_pck.not_informed_w')::varchar(100));
	exception
	when others then
		dt_retorno_w := null;
	end;

	return dt_retorno_w;
END;

$body$
