CREATE OR REPLACE PROCEDURE configura_psa_tws_pack.configure_psa_tws_physician ( nm_attempts datasource.vl_allowed_login_attempts%type, nm_email_admin subject.ds_email%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
