nm_role_physician       role.nm_role%type := 'physician';

ds_role_guest           role.ds_role%TYPE := 'Guest';
nm_role_guest           role.nm_role%TYPE := 'guest';


BEGIN

--create application and datasouce
SELECT * FROM configura_psa_tws_pack.create_application_datasouce( nm_attempts, nm_email_admin, 'PHYSICIAN', 'physician', id_application, id_datasource ) INTO STRICT id_application, id_datasource;

--Insert PHYSICIAN profile permissions to application
CALL CALL configura_psa_tws_pack.create_permission_role(current_setting('configura_psa_tws_pack.permission_schematic_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_schematic_tag')::permission.ds_tag%TYPE, ds_role_physician, nm_role_physician, id_datasource, id_application, 'N');
CALL CALL configura_psa_tws_pack.create_permission_role(current_setting('configura_psa_tws_pack.permission_schedule_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_schedule_tag')::permission.ds_tag%TYPE, ds_role_physician, nm_role_physician, id_datasource, id_application, 'N');
