CALL CALL configura_psa_tws_pack.create_permission_role(current_setting('configura_psa_tws_pack.permission_professional_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_professional_tag')::permission.ds_tag%TYPE, ds_role_physician,  nm_role_physician, id_datasource, id_application, 'N');
CALL CALL configura_psa_tws_pack.create_permission_role(current_setting('configura_psa_tws_pack.permission_utils_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_utils_tag')::permission.ds_tag%TYPE, ds_role_physician,  nm_role_physician, id_datasource, id_application, 'N');
CALL CALL configura_psa_tws_pack.create_permission_role(current_setting('configura_psa_tws_pack.permission_person_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_person_tag')::permission.ds_tag%TYPE, ds_role_physician,  nm_role_physician, id_datasource, id_application, 'N');

