CALL CALL configura_psa_tws_pack.create_permission_role( current_setting('configura_psa_tws_pack.permission_subj_create_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_subj_create_tag')::permission.ds_tag%TYPE, ds_role_guest, nm_role_guest, id_datasource, id_application, 'S' );
CALL CALL configura_psa_tws_pack.create_permission_role( current_setting('configura_psa_tws_pack.permission_subj_retrie_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_subj_retrie_tag')::permission.ds_tag%TYPE, ds_role_guest, nm_role_guest, id_datasource, id_application, 'S' );
CALL CALL configura_psa_tws_pack.create_permission_role( current_setting('configura_psa_tws_pack.permission_subj_cons_give_desc')::permission.ds_tag%TYPE, current_setting('configura_psa_tws_pack.permission_subj_cons_give_tag')::permission.ds_tag%TYPE, ds_role_guest, nm_role_guest, id_datasource, id_application, 'S' );
