CREATE OR REPLACE PROCEDURE configure_psa_tws_pack.configure_psa_tws_results ( nm_attempts client.vl_allowed_login_attempts%type ) AS $body$
DECLARE


id_application      	application.id%type;
id_datasource       	datasource.id%type;

ds_role_referphysician	role.ds_role%TYPE := 'ResultsPortal';
nm_role_referphysician	role.nm_role%TYPE := 'resultsportal';

ds_role_cli_auth_refphysician role.ds_role%TYPE := 'CliAuthResultsPortal';
nm_role_cli_auth_refphysician role.nm_role%TYPE := 'cliAuthResultsPortal';


BEGIN

/*++++++++++++++++++++++++++++++++++++++++++++++ Create application and datasouce to RESULTSPORTAL  +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/

id_application  := configure_psa_tws_pack.create_application_client( nm_attempts, 'RESULTSPORTAL', 'resultsportal', 'WEBSUITE', 'websuite', id_application );
CALL configure_psa_tws_pack.create_role( ds_role_referphysician,  nm_role_referphysician, id_application, 'N');

--Create REFERPHYSICIAN

CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_email_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_email_tag')::permission.ds_tag%TYPE, ds_role_referphysician, nm_role_referphysician,  id_application, 'N');
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_location_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_location_tag')::permission.ds_tag%TYPE, ds_role_referphysician, nm_role_referphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_reports_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_reports_tag')::permission.ds_tag%TYPE, ds_role_referphysician, nm_role_referphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_notification_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_notification_tag')::permission.ds_tag%TYPE, ds_role_referphysician, nm_role_referphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_utils_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_utils_tag')::permission.ds_tag%TYPE, ds_role_referphysician,  nm_role_referphysician, id_application, 'N');
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_person_adm_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_person_adm_tag')::permission.ds_tag%TYPE, ds_role_referphysician,  nm_role_referphysician, id_application, 'N');

--Permission Auth

CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_subj_create_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_subj_create_tag')::permission.ds_tag%TYPE, ds_role_cli_auth_refphysician, nm_role_cli_auth_refphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_subj_retrie_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_subj_retrie_tag')::permission.ds_tag%TYPE, ds_role_cli_auth_refphysician, nm_role_cli_auth_refphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_subj_cons_give_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_subj_cons_give_tag')::permission.ds_tag%TYPE, ds_role_cli_auth_refphysician, nm_role_cli_auth_refphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_subj_acti_stat_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_subj_acti_stat_tag')::permission.ds_tag%TYPE, ds_role_cli_auth_refphysician, nm_role_cli_auth_refphysician,  id_application, 'N' );
CALL CALL configure_psa_tws_pack.create_permission_role( current_setting('configure_psa_tws_pack.permission_subj_full_desc')::permission.ds_tag%TYPE, current_setting('configure_psa_tws_pack.permission_subj_full_tag')::permission.ds_tag%TYPE, ds_role_cli_auth_refphysician, nm_role_cli_auth_refphysician,  id_application, 'N' );

CALL configure_psa_tws_pack.create_client_role('RESULTSPORTAL', ds_role_cli_auth_refphysician);
commit;

END;

