    /*
     * Procedure to update if the person gives the consent or not
     */
CREATE OR REPLACE PROCEDURE consentimento_privacidade_pck.alterar_aceite_item ( nr_seq_pf_privac_item_p pessoa_fisica_privac_item.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

        dt_liberacao_w Pessoa_fisica_privacidade.dt_liberacao%type;

BEGIN
        begin
		select 	pfp.dt_liberacao 
			into STRICT 	dt_liberacao_w 
			from 	pessoa_fisica_privacidade pfp
			inner join pessoa_fisica_privac_item pfpi on pfpi.nr_seq_pf_privacidade = pfp.nr_sequencia
			where 	pfpi.nr_sequencia = nr_seq_pf_privac_item_p;
		
		exception
		when others then
		dt_liberacao_w := null;
       end;

	if (coalesce(dt_liberacao_w::text, '') = '') then
		update	pessoa_fisica_privac_item
		set 	ie_aceite = (case when UPPER(ie_aceite) = 'N' then 'S' else 'N' end),
					nm_usuario = nm_usuario_p,
					dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_seq_pf_privac_item_p;

		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
		COMMIT;
		end if;
	end if;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
