CREATE OR REPLACE PROCEDURE convert_schematic_to_bo_pck.copy_crud_rules (nr_seq_obj_old_p bigint, nr_seq_obj_new_p bigint) AS $body$
DECLARE

 
	c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	opcoes_crud 
	where	nr_seq_objeto_schematic = nr_seq_obj_old_p;

	c01_w	c01%rowtype;

	opcoes_crud_w	opcoes_crud%rowtype;
	nr_seq_crud_w	opcoes_crud.nr_sequencia%type;

	
BEGIN 
 
	open C01;
	loop 
	fetch C01 into 
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		select	* 
		into STRICT	opcoes_crud_w 
		from	opcoes_crud 
		where	nr_sequencia	= c01_w.nr_sequencia;
 
		select	nextval('opcoes_crud_seq') 
		into STRICT	nr_seq_crud_w 
		;
 
		opcoes_crud_w.nr_sequencia		:= nr_seq_crud_w;
		opcoes_crud_w.nr_seq_objeto_schematic	:= nr_seq_obj_new_p;
		opcoes_crud_w.nm_usuario		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		opcoes_crud_w.nm_usuario_nrec		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		opcoes_crud_w.dt_atualizacao		:= clock_timestamp();
		opcoes_crud_w.dt_atualizacao_nrec	:= clock_timestamp();
 
		insert into opcoes_crud values (opcoes_crud_w.*);
 
		CALL convert_schematic_to_bo_pck.copy_regra_condicao(c01_w.nr_sequencia, nr_seq_crud_w, null,null,null,null,null,null,null,null);
 
	end loop;
	close C01;
 
	end;
	--FIM REGRAS DE CRUD DO OBJETO 
 
