	nr_seq_obj_wdlg_new_w		objeto_schematic.nr_sequencia%type;
	nr_seq_dic_obj_msg_new_w	dic_objeto.nr_sequencia%type;
		
	
BEGIN 
	 
	open C01;
	loop 
	fetch C01 into 
