		select	nextval('obj_schematic_evento_acao_seq') 
		into STRICT	nr_seq_evento_acao_w 
		;
	 
		obj_schematic_evento_acao_w.nr_sequencia	:= nr_seq_evento_acao_w;
		obj_schematic_evento_acao_w.nr_seq_obj_evento	:= nr_seq_evento_new_p;
		obj_schematic_evento_acao_w.nm_usuario		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		obj_schematic_evento_acao_w.nm_usuario_nrec	:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		obj_schematic_evento_acao_w.dt_atualizacao	:= clock_timestamp();
		obj_schematic_evento_acao_w.dt_atualizacao_nrec	:= clock_timestamp();
		if (c01_w.nr_seq_obj_recarregar IS NOT NULL AND c01_w.nr_seq_obj_recarregar::text <> '') then 
			obj_schematic_evento_acao_w.nr_seq_obj_recarregar	:= convert_schematic_to_bo_pck.get_seq_obj_new(c01_w.nr_seq_obj_recarregar);
		end if;
		if (c01_w.nr_seq_obj_multiselect IS NOT NULL AND c01_w.nr_seq_obj_multiselect::text <> '') then 
