		end if;
		if (c01_w.nr_seq_obj_wdlg IS NOT NULL AND c01_w.nr_seq_obj_wdlg::text <> '') then 
			--to_do, copiar os wdlgs chamados pelos eventos 
			nr_seq_obj_wdlg_new_w := convert_schematic_to_bo_pck.copy_schematic_wdlg(current_setting('convert_schematic_to_bo_pck.nr_seq_funcao_schematic_w')::funcao_schematic.nr_sequencia%type, current_setting('convert_schematic_to_bo_pck.nr_seq_bo_w')::business_object.nr_sequencia%type, c01_w.nr_seq_obj_wdlg, nr_seq_obj_wdlg_new_w);
