			obj_schematic_evento_acao_w.nr_seq_dic_obj_msg		:= nr_seq_dic_obj_msg_new_w;
		end if;
		 
		insert into obj_schematic_evento_acao values (obj_schematic_evento_acao_w.*);
		 
		CALL convert_schematic_to_bo_pck.copy_obj_schematic_ativ( null,null, c01_w.nr_sequencia, nr_seq_evento_acao_w);
	 
