CREATE OR REPLACE PROCEDURE convert_schematic_to_bo_pck.copy_obj_schematic_ativ (nr_seq_obj_old_p bigint, nr_seq_obj_new_p bigint, nr_seq_obj_evt_acao_old_p bigint, nr_seq_obj_evt_acao_new_p bigint) AS $body$
DECLARE

 
	obj_schematic_ativ_param_w	obj_schematic_ativ_param%rowtype;
	nr_sequencia_w			obj_schematic_ativ_param.nr_sequencia%type;
	nr_seq_ativ_w			obj_schematic_ativ_param.nr_sequencia%type;

	c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	obj_schematic_ativ_param a 
	where	a.nr_seq_objeto	= nr_seq_obj_old_p 
	and	coalesce(nr_seq_obj_evt_acao_old_p::text, '') = '' 
	
union
 
	SELECT	nr_sequencia 
	from	obj_schematic_ativ_param a 
	where	a.nr_seq_obj_evt_acao	= nr_seq_obj_evt_acao_old_p 
	and	coalesce(nr_seq_obj_old_p::text, '') = '';

	
BEGIN 
 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_ativ_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		select	a.* 
		into STRICT	obj_schematic_ativ_param_w 
		from	obj_schematic_ativ_param a 
		where	a.nr_sequencia	= nr_seq_ativ_w;
 
		select	nextval('obj_schematic_ativ_param_seq') 
		into STRICT	nr_sequencia_w 
		;
 
		obj_schematic_ativ_param_w.nr_seq_objeto	:= nr_seq_obj_new_p;
		obj_schematic_ativ_param_w.nr_seq_obj_evt_acao	:= nr_seq_obj_evt_acao_new_p;
		obj_schematic_ativ_param_w.nr_sequencia		:= nr_sequencia_w;
		obj_schematic_ativ_param_w.nm_usuario		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		obj_schematic_ativ_param_w.nm_usuario_nrec	:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		obj_schematic_ativ_param_w.dt_atualizacao	:= clock_timestamp();
		obj_schematic_ativ_param_w.dt_atualizacao_nrec	:= clock_timestamp();
		if (obj_schematic_ativ_param_w.nr_seq_obj_ref IS NOT NULL AND obj_schematic_ativ_param_w.nr_seq_obj_ref::text <> '') then 
			obj_schematic_ativ_param_w.nr_seq_obj_ref	:= coalesce(convert_schematic_to_bo_pck.get_seq_obj_new(obj_schematic_ativ_param_w.nr_seq_obj_ref),null);
		else 
			obj_schematic_ativ_param_w.nr_seq_obj_ref 	:= null;
		end if;
 
		insert into obj_schematic_ativ_param values (obj_schematic_ativ_param_w.*);
 
	end loop;
	close C01;
 
	END;

$body$
