CREATE OR REPLACE PROCEDURE convert_schematic_to_bo_pck.copy_obj_schematic_evento ( nr_seq_obj_sch_old_p bigint, nr_seq_obj_sch_new_p bigint, nr_seq_dic_obj_old_p bigint, nr_seq_dic_obj_new_p bigint) AS $body$
DECLARE

						 
	c01 CURSOR FOR 
	SELECT	a.* --BTN 
	from	obj_schematic_evento a 
	where	nr_seq_objeto		= nr_seq_obj_sch_old_p 
	and	coalesce(nr_seq_dic_objeto::text, '') = ''	 
	and	coalesce(nr_seq_dic_obj_old_p::text, '') = '' 
	
union
 
	SELECT	a.* --Menu Item 
	from	obj_schematic_evento a 
	where	nr_seq_objeto		= nr_seq_obj_sch_old_p 
	and	nr_seq_dic_objeto	= nr_seq_dic_obj_old_p;
	
	c01_w		c01%rowtype;
	
	obj_schematic_evento_w		obj_schematic_evento%rowtype;
	nr_seq_evento_w			obj_schematic_evento.nr_sequencia%type;
	
	
BEGIN	 
	 
	open C01;
	loop 
	fetch C01 into 
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	 
		select	* 
		into STRICT	obj_schematic_evento_w 
		from	obj_schematic_evento 
		where	nr_sequencia	= c01_w.nr_sequencia;
		 
		select	nextval('obj_schematic_evento_seq') 
		into STRICT	nr_seq_evento_w 
		;
		 
		obj_schematic_evento_w.nr_sequencia		:= nr_seq_evento_w;
		obj_schematic_evento_w.nr_seq_objeto		:= nr_seq_obj_sch_new_p;
		obj_schematic_evento_w.nr_seq_dic_objeto	:= nr_seq_dic_obj_new_p;
		obj_schematic_evento_w.nm_usuario		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		obj_schematic_evento_w.nm_usuario_nrec		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		obj_schematic_evento_w.dt_atualizacao		:= clock_timestamp();
		obj_schematic_evento_w.dt_atualizacao_nrec	:= clock_timestamp();
 
		insert into obj_schematic_evento values (obj_schematic_evento_w.*);
		 
		CALL convert_schematic_to_bo_pck.copy_obj_evento_acao(c01_w.nr_sequencia, nr_seq_evento_w);
	 
	end loop;
	close C01;
	 
	end;
	--FIM EVENTOS DE MENU ITENS E BOTÕES DO WDLG 
	 
	--AÇÕES DOS EVENTOS DO SCHEMATIC 
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
