CREATE OR REPLACE PROCEDURE convert_schematic_to_bo_pck.copy_regra_condicao ( nr_seq_opcao_crud_old_p bigint, nr_seq_opcao_crud_new_p bigint, nr_seq_objeto_schem_old_p bigint, nr_seq_objeto_schem_new_p bigint, nr_seq_dic_obj_old_p bigint, nr_seq_dic_obj_new_p bigint, nr_seq_legenda_old_p bigint, nr_seq_obj_sch_leg_old_p bigint, nr_seq_obj_sch_leg_new_p bigint, nr_seq_tasy_img_old_p bigint) AS $body$
DECLARE

 
	c01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	regra_condicao 
	where	nr_seq_opcao_crud	= nr_seq_opcao_crud_old_p 
	and	nr_seq_opcao_crud_old_p	> 0 
	
union
 
	SELECT	nr_sequencia 
	from	regra_condicao 
	where	nr_seq_objeto_schematic	= nr_seq_objeto_schem_old_p 
	and	nr_seq_objeto = nr_seq_dic_obj_old_p 
	and	nr_seq_objeto_schem_old_p > 0 
	and	nr_seq_dic_obj_old_p > 0 
	
union
 
	select	nr_sequencia 
	from	regra_condicao 
	where	nr_seq_legenda	= nr_seq_legenda_old_p 
	and	nr_seq_obj_sch_leg = nr_seq_obj_sch_leg_old_p 
	and	nr_seq_legenda_old_p > 0 
	and	nr_seq_obj_sch_leg_old_p > 0 
	
union
 
	select	nr_sequencia 
	from	regra_condicao 
	where	nr_seq_obj_sch_leg = nr_seq_obj_sch_leg_old_p 
	and	nr_seq_tasy_img = nr_seq_tasy_img_old_p 
	and	nr_seq_obj_sch_leg_old_p > 0 
	and	nr_seq_tasy_img_old_p > 0;

	c01_w		c01%rowtype;

	c02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	regra_condicao_item 
	where	nr_seq_regra	= c01_w.nr_sequencia;

	c02_w			c02%rowtype;

	regra_condicao_w	regra_condicao%rowtype;
	nr_seq_regra_w		regra_condicao.nr_sequencia%type;
	regra_condicao_item_w	regra_condicao_item%rowtype;
	nr_seq_regra_item_w	regra_condicao_item.nr_sequencia%type;

	
BEGIN 
 
	open C01;
	loop 
	fetch C01 into 
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		select	* 
		into STRICT	regra_condicao_w 
		from	regra_condicao 
		where	nr_sequencia	= c01_w.nr_sequencia;
 
		select	nextval('regra_condicao_seq') 
		into STRICT	nr_seq_regra_w 
		;
 
		regra_condicao_w.nr_sequencia			:= nr_seq_regra_w;
		regra_condicao_w.nr_seq_opcao_crud		:= nr_seq_opcao_crud_new_p;
		regra_condicao_w.nr_seq_objeto_schematic	:= nr_seq_objeto_schem_new_p;
		regra_condicao_w.nr_seq_objeto			:= nr_seq_dic_obj_new_p;
		regra_condicao_w.nr_seq_obj_sch_leg		:= nr_seq_obj_sch_leg_new_p;
		regra_condicao_w.nm_usuario			:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		regra_condicao_w.nm_usuario_nrec		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		regra_condicao_w.dt_atualizacao			:= clock_timestamp();
		regra_condicao_w.dt_atualizacao_nrec		:= clock_timestamp();
 
		insert into regra_condicao values (regra_condicao_w.*);
 
		open C02;
		loop 
		fetch C02 into 
			c02_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
 
			select	* 
			into STRICT	regra_condicao_item_w 
			from	regra_condicao_item 
			where	nr_sequencia	= c02_w.nr_sequencia;
 
			select	nextval('regra_condicao_item_seq') 
			into STRICT	nr_seq_regra_item_w 
			;
 
			regra_condicao_item_w.nr_sequencia			:= nr_seq_regra_item_w;
			regra_condicao_item_w.nr_seq_regra			:= nr_seq_regra_w;
			regra_condicao_item_w.nm_usuario			:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
			regra_condicao_item_w.nm_usuario_nrec			:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
			regra_condicao_item_w.dt_atualizacao			:= clock_timestamp();
			regra_condicao_item_w.dt_atualizacao_nrec		:= clock_timestamp();
 
			if (regra_condicao_item_w.nr_seq_objeto_schematic IS NOT NULL AND regra_condicao_item_w.nr_seq_objeto_schematic::text <> '') then 
				regra_condicao_item_w.nr_seq_objeto_schematic	:= convert_schematic_to_bo_pck.get_seq_obj_new(regra_condicao_item_w.nr_seq_objeto_schematic);
			end if;
			 
			insert into regra_condicao_item values (regra_condicao_item_w.*);
 
		end loop;
		close C02;
 
	end loop;
	close C01;
 
	END;

$body$
