SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE convert_schematic_to_bo_pck.copy_schematic_legenda (nr_seq_obj_old_p bigint, nr_seq_obj_new_p bigint) AS $body$
DECLARE

 
	c01 CURSOR FOR 
	SELECT	* 
	from	objeto_schematic_legenda 
	where	nr_seq_objeto = nr_seq_obj_old_p;

	c01_w	c01%rowtype;

	c02 CURSOR FOR 
	SELECT	* 
	from	tasy_padrao_cor a 
	where	a.nr_seq_legenda = c01_w.nr_seq_legenda 
	and	exists (SELECT	1 
		from	regra_condicao x 
		where	x.nr_seq_obj_sch_leg	= c01_w.nr_sequencia 
		and	x.nr_seq_legenda	= a.nr_sequencia);

	c02_w	c02%rowtype;

	c03 CURSOR FOR 
	SELECT	* 
	from	tasy_padrao_imagem a 
	where	a.nr_seq_legenda = c01_w.nr_seq_legenda 
	and	exists (SELECT	1 
		from	regra_condicao x 
		where	x.nr_seq_obj_sch_leg	= c01_w.nr_sequencia 
		and	x.nr_seq_tasy_img	= a.nr_sequencia);

	c03_w	c03%rowtype;

	objeto_schematic_legenda_w	objeto_schematic_legenda%rowtype;
	nr_seq_legenda_w		objeto_schematic_legenda.nr_sequencia%type;

	
BEGIN 
 
	open C01;
	loop 
	fetch C01 into 
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		select	* 
		into STRICT	objeto_schematic_legenda_w 
		from	objeto_schematic_legenda 
		where	nr_sequencia	= c01_w.nr_sequencia;
 
		select	nextval('objeto_schematic_legenda_seq') 
		into STRICT	nr_seq_legenda_w 
		;
 
		objeto_schematic_legenda_w.nr_sequencia			:= nr_seq_legenda_w;
		objeto_schematic_legenda_w.nr_seq_objeto		:= nr_seq_obj_new_p;
		objeto_schematic_legenda_w.nm_usuario			:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		objeto_schematic_legenda_w.nm_usuario_nrec		:= current_setting('convert_schematic_to_bo_pck.nm_usuario_w')::usuario.nm_usuario%type;
		objeto_schematic_legenda_w.dt_atualizacao		:= clock_timestamp();
		objeto_schematic_legenda_w.dt_atualizacao_nrec		:= clock_timestamp();
 
		insert into objeto_schematic_legenda values (objeto_schematic_legenda_w.*);
 
		open C02;
		loop 
		fetch C02 into 
			c02_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
 
			CALL convert_schematic_to_bo_pck.copy_regra_condicao(null, null, null,null,null,null,c02_w.nr_sequencia,c01_w.nr_sequencia,nr_seq_legenda_w,null);
 
		end loop;
		close C02;
 
		open C03;
		loop 
		fetch C03 into 
			c03_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
 
			CALL convert_schematic_to_bo_pck.copy_regra_condicao(null, null, null,null,null,null,null,c01_w.nr_sequencia,nr_seq_legenda_w,c03_w.nr_sequencia);
 
		end loop;
		close C03;
 
	end loop;
	close C01;
 
	end;
	--FIM LEGENDAS DO OBJETO SCHEMATIC 
 
	--REGRA_CONDICAO DE CRUD, LEGENDA, MENU ITEM 
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
