SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE cpoe_calc_valores_sol_md_pck.calcula_hora_fase_etapas_md (ie_operacao_p text, qt_operacao_p bigint, ie_continuo_p text, qt_solucao_total_p bigint, qt_dosagem_p bigint, qt_tempo_aplicacao_p bigint, qt_hora_fase_p INOUT bigint, nr_etapas_interv_p INOUT bigint, nr_etapas_val_sol_p INOUT bigint ) AS $body$
BEGIN
    if (ie_operacao_p in ('X', 'F','V')) then
      qt_hora_fase_p	:= dividir_md(24,qt_operacao_p);
    elsif (ie_continuo_p = 'S') and (ie_operacao_p = 'H') and (qt_solucao_total_p IS NOT NULL AND qt_solucao_total_p::text <> '') and (qt_solucao_total_p > 0) and (qt_dosagem_p IS NOT NULL AND qt_dosagem_p::text <> '') and (qt_dosagem_p > 0) then
      qt_hora_fase_p	:= dividir_md( qt_solucao_total_p, qt_dosagem_p );
    elsif (ie_operacao_p = 'H') then
      qt_hora_fase_p := qt_operacao_p;
    else
      qt_hora_fase_p := dividir_md(qt_dosagem_p,qt_operacao_p); -- MD 1
    end if;

    if (qt_hora_fase_p > qt_tempo_aplicacao_p ) then
      qt_hora_fase_p	:= qt_tempo_aplicacao_p;
    end if;

    nr_etapas_interv_p   := dividir_md(qt_tempo_aplicacao_p,qt_hora_fase_p);
    nr_etapas_val_sol_p  := ceil(nr_etapas_interv_p);
  end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
