CREATE OR REPLACE PROCEDURE cpoe_calc_valores_sol_md_pck.calcula_unid_medida_padrao_md (ie_tipo_dosagem_p text, qt_Gotas_p INOUT bigint, qt_Tempo_p INOUT bigint ) AS $body$
BEGIN
    if (upper(ie_tipo_dosagem_p) = 'GTM') then
      qt_Gotas_p	:= 20;
      qt_Tempo_p	:= 60;
    elsif (upper(ie_tipo_dosagem_p) = 'MLH') then
      qt_Gotas_p	:= 1;
      qt_Tempo_p	:= 1;
    else
      qt_Gotas_p	:= 60;
      qt_Tempo_p	:= 60;
    end if;
  end;

$body$
