CREATE OR REPLACE FUNCTION cpoe_dial_order_json_pck.get_dial_message (nr_cpoe_dial_p bigint, ie_order_control_p text, nr_entity_identifier_p bigint) RETURNS PHILIPS_JSON AS $body$
DECLARE

	json_return_w		philips_json;
	json_item_list_w   	philips_json_list;

	
BEGIN
	
  json_item_list_w	:= cpoe_dial_order_json_pck.get_dial_data(nr_cpoe_dial_p);
	
	if (json_item_list_w.count > 0) then
		json_return_w	:= philips_json();
		json_return_w		:= cpoe_dial_order_json_pck.get_default_message(nr_cpoe_dial_p);
		json_return_w := cpoe_dial_order_json_pck.add_json_value(json_return_w, 'orderControl', ie_order_control_p);
		json_return_w := cpoe_dial_order_json_pck.add_json_value(json_return_w, 'entityidentifier', nr_entity_identifier_p);
		json_return_w := cpoe_dial_order_json_pck.add_json_value(json_return_w, 'hl7MsgType', 'OMP');
		
		json_return_w.put('dialList', json_item_list_w.to_json_value());
	end if;
	
	return json_return_w;
	
	END;

