CREATE OR REPLACE FUNCTION cpoe_listagg_pck.tab_to_string (varchar2_tab_p strarray, delimiter_p text default ';') RETURNS varchar AS $body$
DECLARE

		l_string     varchar(32767);
	
BEGIN
		if coalesce(varchar2_tab_p.first::text, '') = '' then
			return '';
		else
			for i in varchar2_tab_p.first .. varchar2_tab_p.last loop
				if i != varchar2_tab_p.first then
					l_string := l_string || delimiter_p;
				end if;
				l_string := l_string || varchar2_tab_p(i);
			end loop;

			return l_string;
		end if;
	END;

