CREATE OR REPLACE FUNCTION cpoe_mat_json_pck.get_mat_message (nr_cpoe_mat_p bigint, ie_order_control_p text, nr_entity_identifier_p bigint) RETURNS PHILIPS_JSON AS $body$
DECLARE

	json_return_w		philips_json;
	json_item_list_w   	philips_json_list;
	ie_hl7_msg_type_w	varchar(3);
	
BEGIN
	
	json_item_list_w	:= cpoe_mat_json_pck.get_material(nr_cpoe_mat_p);
	ie_hl7_msg_type_w := 'OMP';
	
	if (json_item_list_w.count > 0) then
		json_return_w	:= philips_json();
		json_return_w		:= cpoe_mat_json_pck.get_default_message(nr_cpoe_mat_p);
		json_return_w := cpoe_mat_json_pck.add_json_value(json_return_w, 'orderControl', ie_order_control_p);
		json_return_w := cpoe_mat_json_pck.add_json_value(json_return_w, 'entityidentifier', nr_entity_identifier_p);
		json_return_w := cpoe_mat_json_pck.add_json_value(json_return_w, 'hl7MsgType', ie_hl7_msg_type_w);
		
		json_return_w.put('medList', json_item_list_w.to_json_value());
	end if;
	
	return json_return_w;
	
	END;

