CREATE OR REPLACE FUNCTION cpoe_mat_json_pck.get_solution_prescr_message (nr_cpoe_mat_p bigint, nr_seq_sol_evento_p bigint, nr_seq_solucao_p bigint, nr_etapa_evento_p bigint, nr_prescricao_p bigint, nm_usuario_p text) RETURNS text AS $body$
DECLARE

	ds_json_out_w		text;
	json_mat_w	philips_json;
  json_item_list_w   	philips_json_list;

  ie_order_control_w varchar(2);
  nr_entity_identifier_w bigint;
	
	
BEGIN

  if (cpoe_mat_json_pck.existsCpoeIntegracaoMat(nr_cpoe_mat_p)) then
    ie_order_control_w := 'XO';
  else
    ie_order_control_w := 'NW';
  end if;

  nr_entity_identifier_w := cpoe_mat_json_pck.getCpoeIntegracaoAckMat(nr_cpoe_mat_p, nm_usuario_p, nr_entity_identifier_w);
		
	json_mat_w		:= cpoe_mat_json_pck.get_mat_message(nr_cpoe_mat_p, ie_order_control_w, nr_entity_identifier_w);
	
	if (coalesce(json_mat_w::text, '') = '') then
		return null;
	end if;

  json_item_list_w	:= cpoe_mat_json_pck.get_solution_prescr_changes(nr_seq_sol_evento_p, nr_seq_solucao_p,
    nr_etapa_evento_p, nr_prescricao_p);
  json_mat_w.put('changeList', json_item_list_w.to_json_value());
	
	dbms_lob.createtemporary( ds_json_out_w, true);
	json_mat_w.(ds_json_out_w);
	
	return ds_json_out_w;
	END;

$body$
