CREATE OR REPLACE PROCEDURE cpoe_prev_hor_futuros_md_pck.calc_remov_hor_nao_vigent_md (ds_horarios_p INOUT text, ds_horario_p INOUT text, dt_inicio_p timestamp, dt_fim_p timestamp, dt_horario_p INOUT timestamp, dt_inicio_base_p timestamp, ds_horarios_vigente_p INOUT text ) AS $body$
DECLARE

    k varchar(255);

BEGIN
    
    while	(ds_horarios_p IS NOT NULL AND ds_horarios_p::text <> '') loop
      begin
      k := position(' ' in ds_horarios_p);
      if (k > 1) and ((substr(ds_horarios_p, 1, k -1) IS NOT NULL AND (substr(ds_horarios_p, 1, k -1))::text <> '')) then
        ds_horario_p		:= substr(ds_horarios_p, 1, k-1);
        ds_horario_p		:= replace(ds_horario_p, ' ','');
        ds_horarios_p		:= substr(ds_horarios_p, k + 1, 2000);
      elsif (ds_horarios_p IS NOT NULL AND ds_horarios_p::text <> '') then
        ds_horario_p 		:= replace(ds_horarios_p,' ','');
        ds_horarios_p		:= '';
      end if;

      if (to_date(to_char(dt_inicio_p, 'dd/mm/yyyy') || ' ' || ds_horario_p, 'dd/mm/yyyy hh24:mi:ss') >= dt_inicio_p) then
        dt_horario_p := to_date(to_char(dt_inicio_p, 'dd/mm/yyyy') || ' ' || ds_horario_p, 'dd/mm/yyyy hh24:mi:ss');
      elsif (to_date(to_char(dt_fim_p, 'dd/mm/yyyy') || ' ' || ds_horario_p, 'dd/mm/yyyy hh24:mi') >= dt_inicio_p) then
        dt_horario_p := to_date(to_char(dt_fim_p, 'dd/mm/yyyy') || ' ' || ds_horario_p, 'dd/mm/yyyy hh24:mi:ss');
      end if;

      if (dt_horario_p > dt_inicio_base_p) then
        ds_horarios_vigente_p := ds_horarios_vigente_p || ' ' || ds_horario_p;
      end if;
      end;
    end loop;
  end;


$body$
