CREATE OR REPLACE PROCEDURE cpoe_prev_hor_futuros_md_pck.calcula_dt_inicio_tempo_hor_md (ds_dias_utilizacao_p text, dt_inicio_p INOUT timestamp, qt_tempo_horario_p INOUT bigint, dt_inicio_ref_p timestamp ) AS $body$
BEGIN
    if (ds_dias_utilizacao_p IS NOT NULL AND ds_dias_utilizacao_p::text <> '') then
      dt_inicio_p			   	:= dt_inicio_p+1;
      qt_tempo_horario_p	:= coalesce(qt_tempo_horario_p,0)+24;
      loop
        begin
        qt_tempo_horario_p	:= qt_tempo_horario_p + 24;
        dt_inicio_p			    := dt_inicio_p + 1;
        if (qt_tempo_horario_p > 5000) then
          return;
        end if;
        end;
      exit when(dt_inicio_p > dt_inicio_ref_p) and (obter_se_valor_contido(pkg_date_utils.get_WeekDay(dt_inicio_p),ds_dias_utilizacao_p) = 'S');
      end loop;

    else
      dt_inicio_p		:= dt_inicio_p-5;
    end if;
  end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
