CREATE OR REPLACE PROCEDURE cpoe_prev_hor_futuros_md_pck.calcula_prox_adm_por_horas_md (dt_inicio_p INOUT timestamp, dt_inicio_ref_p timestamp, qt_tempo_horario_p INOUT bigint, qt_ocorrencia_intervalo_p bigint, qt_horas_ref_p INOUT bigint ) AS $body$
BEGIN
    if (dt_inicio_p < dt_inicio_ref_p) then
      qt_tempo_horario_p := qt_ocorrencia_intervalo_p;

      if (coalesce(qt_tempo_horario_p,0) <= 0) then
        qt_tempo_horario_p := 1;
      end if;

      loop
        begin
        qt_horas_ref_p := coalesce(qt_horas_ref_p,0) + coalesce(qt_tempo_horario_p,0);
        dt_inicio_p    := dt_inicio_p + dividir_sem_round_md(qt_tempo_horario_p,24);
        end;
      exit when(dt_inicio_p > dt_inicio_ref_p);
      end loop;
    end if;
  end;


$body$
