CREATE OR REPLACE FUNCTION cpoe_prev_hor_futuros_md_pck.obter_dt_hora_fim_md (dt_horario_p timestamp, qt_tempo_horario_p bigint, dt_fim_p timestamp, nr_param_control_p bigint ) RETURNS timestamp AS $body$
DECLARE


      dt_hora_fim_w timestamp;

BEGIN
    if (qt_tempo_horario_p > 0) and (nr_param_control_p = 0) then 
      dt_hora_fim_w			:= dt_horario_p + dividir_sem_round_md(qt_tempo_horario_p,24);

      if (dt_hora_fim_w > dt_fim_p) then
        dt_hora_fim_w	:= dt_fim_p;
      end if;

    elsif (coalesce(qt_tempo_horario_p,0) = 0) then  
      dt_hora_fim_w := dt_fim_p;

      if (nr_param_control_p = 1) then
        if (dt_fim_p <= dt_horario_p) then
          dt_hora_fim_w := dt_horario_p + 1;
        end if;

      elsif (nr_param_control_p = 2) then
        if (coalesce(dt_fim_p::text, '') = '') or (dt_fim_p > dt_horario_p+1) then
           dt_hora_fim_w := dt_horario_p+1;
        end if;
      end if;
    end if;

    return dt_hora_fim_w;
  end;


$body$
