CREATE OR REPLACE FUNCTION cpoe_proced_order_json_pck.get_proc_message (nr_cpoe_proc_p bigint, ie_order_control_p text, nr_entity_identifier_p bigint) RETURNS PHILIPS_JSON AS $body$
DECLARE

	json_return_w		philips_json;
	json_item_list_w   	philips_json_list;

  nr_seq_proc_interno_w cpoe_procedimento.nr_seq_proc_interno%type;

	
BEGIN

  json_item_list_w	:= cpoe_proced_order_json_pck.get_proc_data(nr_cpoe_proc_p);
	
	if (json_item_list_w.count > 0) then
		json_return_w	:= philips_json();
		json_return_w		:= cpoe_proced_order_json_pck.get_default_message(nr_cpoe_proc_p);
		json_return_w := cpoe_proced_order_json_pck.add_json_value(json_return_w, 'orderControl', ie_order_control_p);
		json_return_w := cpoe_proced_order_json_pck.add_json_value(json_return_w, 'entityidentifier', nr_entity_identifier_p);

    select nr_seq_proc_interno
    into STRICT nr_seq_proc_interno_w
    from cpoe_procedimento
    where nr_sequencia = nr_cpoe_proc_p;

    if (obter_se_proc_IVC(nr_seq_proc_interno_w) = 'S') then
      json_return_w := cpoe_proced_order_json_pck.add_json_value(json_return_w, 'hl7MsgType', 'OMP');
    else
      json_return_w := cpoe_proced_order_json_pck.add_json_value(json_return_w, 'hl7MsgType', 'ORM');
    end if;
		
		json_return_w.put('procedList', json_item_list_w.to_json_value());
	end if;
	
	return json_return_w;
	
	END;

