			nr_sequencia,
			NR_SEQ_PROC_CPOE,
      DT_ACK_INTERFACE,
      NM_ACK_INTERFACE
			)
		values (
			nr_entity_identifier_p,
			nr_seq_proc_cpoe_p,
      clock_timestamp(),
      nm_usuario_p
		);
