CREATE OR REPLACE PROCEDURE cronograma_padrao_npi.seta_dados_projeto (nr_seq_projeto_p bigint) AS $body$
BEGIN
		current_setting('cronograma_padrao_npi.projeto')::dados_projeto.nr_seq_projeto := nr_seq_projeto_p;

		select	coalesce(min(a.dt_inicio_prev),clock_timestamp()),
			max(cd_estabelecimento)
		into STRICT	current_setting('cronograma_padrao_npi.projeto')::dados_projeto.dt_inicio_prev,
			current_setting('cronograma_padrao_npi.projeto')::dados_projeto.cd_estabelecimento
		from	proj_projeto a
		where	a.nr_sequencia = current_setting('cronograma_padrao_npi.projeto')::dados_projeto.nr_seq_projeto;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
