CREATE OR REPLACE FUNCTION ctb_concil_financeira_pck.formata_atributos_conciliacao (ds_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_retorno_ww	varchar(255);
i		integer;


BEGIN
ds_retorno_w    := substr(replace(replace(replace(replace(replace(replace(replace(Elimina_Acentuacao(ds_campo_p),chr(38),''),'<',''),' ',''),'(',''),')',''),'"',''),'>',''),1,255);

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	begin

	for	i in 1 .. length(ds_retorno_w) loop
		begin
		if	not(substr(ds_retorno_w,i,1) in ('-','+')) then
			ds_retorno_ww	:= ds_retorno_ww || substr(ds_retorno_w,i,1);
		else
			ds_retorno_ww	:= ds_retorno_ww || '|';
		end if;
		end;
	end loop;
	end;
end if;
	
return	ds_retorno_ww || '|';

END;

