CREATE OR REPLACE FUNCTION ctb_contr_patrimonio_bolivia.calcula_depreciacao_atual ( vl_bem_atualizado_p MEMORIA_CALCULO_AITB.VL_ATUALIZADO_BEM%type, vl_taxa_deprec_p PAT_BEM_TAXA.PR_DEPRECIACAO%type ) RETURNS bigint AS $body$
