CREATE OR REPLACE PROCEDURE ctb_online_pck.ctb_excluir_lancamento (nr_seq_lancamento_p bigint, nr_lote_contabil_p bigint, nm_usuario_p text, nr_seq_movimento_p bigint default null, ie_commit_p text default 'S', ds_erro_p INOUT text DEFAULT NULL) AS $body$
DECLARE


C01 CURSOR FOR
    SELECT a.nr_sequencia,
            a.nr_seq_movto_partida,
            a.dt_atualizacao_saldo
    from   ctb_movimento a
    where  a.nr_agrup_sequencial = nr_seq_lancamento_p
    and    a.nr_lote_contabil = nr_lote_contabil_p
    and    coalesce(nr_seq_movimento_p,0) = 0
    and    coalesce(nr_seq_lancamento_p,0) <> 0

union all

    SELECT a.nr_sequencia,
            a.nr_seq_movto_partida,
            a.dt_atualizacao_saldo
    from   ctb_movimento a
    where  a.nr_sequencia = nr_seq_movimento_p
    and    a.nr_lote_contabil = nr_lote_contabil_p
    and    coalesce(nr_seq_movimento_p,0) <> 0
    and    coalesce(nr_seq_lancamento_p,0) = 0
    order  by nr_seq_movto_partida;

c01_w C01%rowtype;


BEGIN

    open C01;
    loop
        fetch C01
            into c01_w;
        EXIT WHEN NOT FOUND; /* apply on C01 */
        begin
            if (c01_w.dt_atualizacao_saldo IS NOT NULL AND c01_w.dt_atualizacao_saldo::text <> '') then
                begin
                    CALL ctb_online_pck.ctb_desatualizar_saldo_movto(c01_w.nr_sequencia,'N',nm_usuario_p, 'N');
                end;
            end if;

            DELETE FROM movimento_contabil_doc WHERE nr_seq_ctb_movto = c01_w.nr_sequencia;

            DELETE FROM ctb_movto_centro_custo WHERE nr_seq_movimento = c01_w.nr_sequencia;

            DELETE FROM ctb_movimento WHERE nr_sequencia = c01_w.nr_sequencia;

        exception when others then
            ds_erro_p := wheb_mensagem_pck.get_texto(1112261);
            rollback;
        end;
    end loop;
    close C01;

    if (ie_commit_p = 'S') then
        commit;
    end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
