CREATE OR REPLACE FUNCTION ctb_parametros_cc_rateio_pck.retorna_ctb_param_cc_rateio ( cd_sequencia_parametro_p parametros_conta_contabil.cd_sequencia_parametro%type, vl_total_rateio_p bigint) RETURNS SETOF T_PARAM_CC_RATEIO_VALOR_DATE AS $body$
DECLARE


t_param_cc_rateio_valor_w	t_param_cc_rateio_valor;
qt_count_w			bigint := 0;
vl_total_rateio_w		double precision;

--Cursor para buscar os registros de regras de rateio por conta e centro de custo
--Nao deve ser alterado o order by, pois ele serve para o ultimo registro ser o com maior percentagem, devido aos arrendondamentos
C_CC_rateio CURSOR FOR
	SELECT	a.nr_sequencia,
		a.cd_conta_receita,
		cd_centro_custo,
		a.pr_rateio
	from	parametros_cc_rateio a
	where	a.cd_sequencia_parametro	= cd_sequencia_parametro_p
	and 	coalesce(a.pr_rateio, 0) > 0
	and 	coalesce(vl_total_rateio_p, 0) > 0
	order by a.pr_rateio;
	
C_CC_rateio_registros_w		C_CC_rateio%rowtype;


BEGIN

vl_total_rateio_w:= 0;


--Select utilizado para variacao de arrendondamento de valores rateados
select	count(1)
into STRICT	qt_count_w
from	parametros_cc_rateio a
where	a.cd_sequencia_parametro	= cd_sequencia_parametro_p
and 	coalesce(a.pr_rateio, 0) > 0
and 	coalesce(vl_total_rateio_p, 0) > 0;

open C_CC_rateio;
loop
fetch C_CC_rateio into	
	C_CC_rateio_registros_w;
EXIT WHEN NOT FOUND; /* apply on C_CC_rateio */
	begin

	t_param_cc_rateio_valor_w.cd_conta_receita	:=	C_CC_rateio_registros_w.cd_conta_receita;
	t_param_cc_rateio_valor_w.cd_centro_custo	:=	C_CC_rateio_registros_w.cd_centro_custo;
	t_param_cc_rateio_valor_w.vl_valor_rateio 	:=	round((C_CC_rateio_registros_w.pr_rateio * vl_total_rateio_p) / 100 , 2);
	vl_total_rateio_w				:=	vl_total_rateio_w + t_param_cc_rateio_valor_w.vl_valor_rateio;
	t_param_cc_rateio_valor_w.nr_seq_par_cc_rateio	:=	C_CC_rateio_registros_w.nr_sequencia;
	
	qt_count_w:= qt_count_w - 1;
	
	--No ultimo registro de rateio deve conter a variacao de valores em decorrer do round
	if (qt_count_w = 0) then
	
		t_param_cc_rateio_valor_w.vl_valor_rateio	:=	t_param_cc_rateio_valor_w.vl_valor_rateio + (vl_total_rateio_p - vl_total_rateio_w);
		
	end if;
	
	RETURN NEXT t_param_cc_rateio_valor_w;
	
	end;
end loop;
close C_CC_rateio;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
