CREATE OR REPLACE PROCEDURE ctb_regras_contabil.comprovante_cache_storage ( nr_lote_contabil_p W_MOVIMENTO_CONTABIL.NR_LOTE_CONTABIL%type, nm_usuario_p text ) AS $body$
DECLARE

	c010 CURSOR FOR
		SELECT	NR_SEQ_AGRUPAMENTO,
			NR_LOTE_CONTABIL,
			NR_CODIGO_CONTROLE
		FROM 	MOVIMENTO_CONTABIL
		WHERE	NR_LOTE_CONTABIL = nr_lote_contabil_p;


BEGIN
	IF (PKG_I18N.GET_USER_LOCALE = 'es_BO') THEN
		BEGIN
			FOR nr_row_w IN c010 LOOP
				INSERT INTO LOG_MOV(
					DT_ATUALIZACAO,
					NM_USUARIO,
					DS_LOG,
					CD_LOG
				) VALUES (
					clock_timestamp(),
					nm_usuario_p,
					(nr_row_w.nr_seq_agrupamento	|| ',' ||
					nr_lote_contabil_p		|| ',' ||
					nr_row_w.nr_codigo_controle),
					667
				);
			END LOOP;
		EXCEPTION
			WHEN OTHERS THEN NULL;
		END;
	END IF;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
