CREATE OR REPLACE FUNCTION ctb_regras_contabil.obter_estorno_tesouraria ( nr_seq_trans_financ_p MOVTO_TRANS_FINANC.NR_SEQ_TRANS_FINANC%type, nr_seq_caixa_p MOVTO_TRANS_FINANC.NR_SEQ_CAIXA%type, nr_seq_cheque_p MOVTO_TRANS_FINANC.NR_SEQ_CHEQUE%type ) RETURNS varchar AS $body$
DECLARE


    nr_codigo_controle_w    MOVTO_TRANS_FINANC.NR_CODIGO_CONTROLE%type;


BEGIN

        SELECT  MAX(NR_CODIGO_CONTROLE)
        INTO STRICT    nr_codigo_controle_w
        FROM    MOVTO_TRANS_FINANC
        WHERE   NR_SEQ_TRANS_FINANC = nr_seq_trans_financ_p
        AND     NR_SEQ_CHEQUE = nr_seq_cheque_p
        AND     coalesce(IE_ESTORNO::text, '') = '';

        RETURN  nr_codigo_controle_w;

    END;

