CREATE OR REPLACE FUNCTION ctb_regras_contabil.obter_estorno_tit_receber ( nr_titulo_p TITULO_RECEBER_LIQ.NR_TITULO%type, nr_seq_liq_origem_p TITULO_RECEBER_LIQ.NR_SEQUENCIA%type ) RETURNS varchar AS $body$
DECLARE


