SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ctb_regras_contabil.restore_comprovante_from_cache ( nr_lote_contabil_p W_MOVIMENTO_CONTABIL.NR_LOTE_CONTABIL%type, nm_usuario_p text ) AS $body$
DECLARE

	c010 CURSOR FOR
		SELECT	DT_ATUALIZACAO,
			NM_USUARIO,
			DS_LOG,
			CD_LOG
		FROM	LOG_MOV
		WHERE	NM_USUARIO = nm_usuario_p
		AND	CD_LOG 	   = 667;

BEGIN
	FOR operacao IN c010 LOOP
		BEGIN
			UPDATE	W_MOVIMENTO_CONTABIL
			SET	NR_CODIGO_CONTROLE	= trim(both REGEXP_SUBSTR(operacao.ds_log,'[^,]+',1, 3))
			WHERE	NR_LOTE_CONTABIL	= (trim(both REGEXP_SUBSTR(operacao.ds_log,'[^,]+',1, 2)))::numeric 
			AND	NR_SEQ_AGRUPAMENTO	= (trim(both REGEXP_SUBSTR(operacao.ds_log,'[^,]+',1, 1)))::numeric;
		EXCEPTION
			WHEN OTHERS THEN NULL;
		END;
	END LOOP;

	DELETE  FROM LOG_MOV
	WHERE	NM_USUARIO = nm_usuario_p
	AND	CD_LOG 	   = 667;

	COMMIT;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
