CREATE OR REPLACE FUNCTION ctb_regras_contabil.verificar_estorno_tesouraria ( nr_sequencia_p MOVTO_TRANS_FINANC.NR_SEQUENCIA%type ) RETURNS varchar AS $body$
DECLARE


    ds_retorno_w    varchar(15);


BEGIN
    
        SELECT  MAX(NR_CODIGO_CONTROLE)
        INTO STRICT    ds_retorno_w
        FROM    MOVTO_TRANS_FINANC
        WHERE   NR_SEQUENCIA =  nr_sequencia_p
        AND     IE_ESTORNO = 'E';

        RETURN ds_retorno_w;

    END;

$body$
LANGUAGE PLPGSQL
