CREATE OR REPLACE PROCEDURE ctb_sped_ecd_pck.gerar_interf_9900_ecd (regra_sped_p INOUT ctb_sped_ecd_pck.regra_sped, nm_usuario_p text) AS $body$
DECLARE


ds_linha_w			varchar(8000);
sep_w				varchar(1) := '|';
tp_registro_w			varchar(15) := '9900';
qt_linhas_w			bigint;

c01 CURSOR FOR
	SELECT  x.vl_dominio cd_registro,
		x.nr_seq_apresent,
		1 qt_linhas
	from	valor_dominio x
	where	x.cd_dominio	= 3508
	and	x.vl_dominio	in ('9900','9990', '9999')
	order by nr_seq_apresent;
	

type vetor_c01 is table of c01%rowtype index by integer;
vetor_c01_w    vetor_c01;

BEGIN
open c01;
	loop fetch c01 bulk collect into vetor_c01_w limit 1000;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		for i in vetor_c01_w.first .. vetor_c01_w.last loop
	
		qt_linhas_w:= vetor_c01_w[i].qt_linhas;

		if (vetor_c01_w[i].cd_registro	= tp_registro_w) then
			qt_linhas_w 	:= regra_sped_p.contador_registro(tp_registro_w);
			qt_linhas_w	:= coalesce(qt_linhas_w,0)	+ 3;
		end if;
		
		ds_linha_w	:= substr(	sep_w || tp_registro_w							||
						sep_w || vetor_c01_w[i].cd_registro					||
						sep_w || qt_linhas_w 	|| sep_w,1,8000);
		
		regra_sped_p.cd_registro_variavel := tp_registro_w;
		regra_sped_p => regra_sped_p := ctb_sped_ecd_pck.inserir_registros_vetor(regra_sped_p => regra_sped_p, ds_linha_p => ds_linha_w);
		
		end loop;
		regra_sped_p => regra_sped_p := ctb_sped_ecd_pck.persistir_registros_em_lote(regra_sped_p => regra_sped_p, nm_usuario_p => nm_usuario_p);
	end loop;
close  c01;
			
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
