CREATE OR REPLACE PROCEDURE ctb_sped_ecd_pck.gerar_interf_k115_ecd (regra_sped_p INOUT ctb_sped_ecd_pck.regra_sped, nm_usuario_p text, cd_empresa_p ctb_sped_controle.cd_empresa%type, dt_situacao_p ctb_sit_especial_empresa.dt_situacao%type) AS $body$
DECLARE


ds_linha_w		varchar(8000);
sep_w			varchar(1) := '|';
tp_registro_w		varchar(15) := 'K115';

c01 CURSOR(
	dt_situacao_pc		ctb_sit_especial_empresa.dt_situacao%type,
	cd_empresa_pc		ctb_sped_controle.cd_empresa%type
	) FOR
	SELECT	sit.ie_condicao_empresa ie_condicao_empresa,
		sit.pr_evento_empresa pr_evento_empresa
	from	ctb_sit_especial_empresa sit
	where	sit.dt_situacao = dt_situacao_pc
	and	sit.cd_empresa = cd_empresa_pc
	order by sit.dt_situacao,
		sit.pr_evento_empresa;

type vetor_c01 is table of c01%rowtype index by integer;
v_vetor_c01_w   vetor_c01;

BEGIN

open c01(
	dt_situacao_pc	=> 	dt_situacao_p,
	cd_empresa_pc 	=>	cd_empresa_p
	);
	loop fetch c01 bulk collect into v_vetor_c01_w limit 1000;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		for i in v_vetor_c01_w.first .. v_vetor_c01_w.last loop
		begin
			ds_linha_w	:= substr(	sep_w || tp_registro_w  			||
							sep_w || cd_empresa_p 				|| 
							sep_w || v_vetor_c01_w[i].ie_condicao_empresa 	||
							sep_w || v_vetor_c01_w[i].pr_evento_empresa 	|| sep_w,1,8000);
			
			regra_sped_p.cd_registro_variavel := tp_registro_w;				
			regra_sped_p => regra_sped_p := ctb_sped_ecd_pck.inserir_registros_vetor(regra_sped_p => regra_sped_p, ds_linha_p => ds_linha_w);
		
			if (regra_sped_p.registros.count >= 1000) then
				regra_sped_p => regra_sped_p := ctb_sped_ecd_pck.persistir_registros_em_lote(regra_sped_p => regra_sped_p, nm_usuario_p => nm_usuario_p);
			end if;
		
		end;
		end loop;
		regra_sped_p => regra_sped_p := ctb_sped_ecd_pck.persistir_registros_em_lote(regra_sped_p => regra_sped_p, nm_usuario_p => nm_usuario_p);
	end loop;
close c01;

END;

