CREATE OR REPLACE PROCEDURE ctb_sped_ecd_pck.inserir_registros_vetor (regra_sped_p INOUT ctb_sped_ecd_pck.regra_sped, ds_linha_p text) AS $body$
DECLARE


nr_indice_w 		bigint;
ds_arquivo_w		varchar(4000);
ds_compl_arquivo_w	varchar(4000);


BEGIN

ds_arquivo_w		:= substr(ds_linha_p,1,4000);
ds_compl_arquivo_w	:= substr(ds_linha_p,4001,4000);
regra_sped_p.qt_linha		:= regra_sped_p.qt_linha	+ 1;
regra_sped_p.nr_seq_registro	:= regra_sped_p.nr_seq_registro 	+ 1;
nr_indice_w := regra_sped_p.registros.count + 1;


regra_sped_p.registros[nr_indice_w].ds_arquivo 		:= ds_arquivo_w;
regra_sped_p.registros[nr_indice_w].ds_arquivo_compl	:= ds_compl_arquivo_w;
regra_sped_p.registros[nr_indice_w].nr_linha 		:= regra_sped_p.qt_linha;
regra_sped_p.registros[nr_indice_w].nr_sequencia 	:= regra_sped_p.nr_seq_registro;
regra_sped_p.registros[nr_indice_w].cd_registro 	:= regra_sped_p.cd_registro_variavel;
regra_sped_p.registros[nr_indice_w].nr_doc_origem	:= regra_sped_p.nr_doc_origem;

regra_sped_p => regra_sped_p := ctb_sped_ecd_pck.totalizar_tipo_registro(regra_sped_p => regra_sped_p);
regra_sped_p.nr_doc_origem := null;
regra_sped_p.cd_registro_variavel := null;

END;

$body$
