CREATE OR REPLACE FUNCTION ctb_uuid_pck.get_uuid_contas_receber (nr_documento_p movimento_contabil_doc.nr_documento%type, nr_seq_doc_compl_p movimento_contabil_doc.nr_seq_doc_compl%type) RETURNS NOTA_FISCAL.NR_NFE_IMP%TYPE AS $body$
DECLARE


nr_nfe_imp_w    nota_fiscal.nr_nfe_imp%type;


BEGIN
    begin
    select  max(nr_nfe_imp)
    into STRICT    nr_nfe_imp_w
    from (
        SELECT  x.nr_nfe_imp nr_nfe_imp
        from    titulo_receber_liq nf,
                titulo_receber t,
                nota_fiscal x
        where   nf.nr_titulo = t.nr_titulo
        and     x.nr_sequencia_ref = t.nr_seq_nf_saida
        and     x.nr_seq_baixa_tit  = nf.nr_sequencia
        and     (x.nr_nfe_imp IS NOT NULL AND x.nr_nfe_imp::text <> '')
        and     nf.nr_titulo =  nr_documento_p
        and     nf.nr_sequencia = nr_seq_doc_compl_p

union

        SELECT  x.nr_nfe_imp nr_nfe_imp
        from    titulo_receber_liq nf,
                titulo_receber t,
                nota_fiscal x,
                nota_fiscal_item z
        where   nf.nr_titulo = t.nr_titulo
        and     z.nr_seq_tit_rec  = nf.nr_sequencia
        and     z.nr_sequencia = x.nr_sequencia
        and     z.nr_titulo = t.nr_titulo
        and     (x.nr_nfe_imp IS NOT NULL AND x.nr_nfe_imp::text <> '')
        and     nf.nr_titulo = nr_documento_p
        and     nf.nr_sequencia = nr_seq_doc_compl_p) alias3;
    exception
    when others then
        nr_nfe_imp_w:= null;
    end;

return  nr_nfe_imp_w;

END;

$body$
